private void tampilkan_data() {
        DefaultTableModel tabelmapel = new DefaultTableModel();
        tabelmapel.addColumn("No Nota");
        tabelmapel.addColumn("No Pelanggan");
        tabelmapel.addColumn("Tanggal Masuk");
        tabelmapel.addColumn("Tanggal Selesai");
        tabelmapel.addColumn("Status");
        tabelmapel.addColumn("Status Bayar");
        tabelmapel.addColumn("Tanggal Bayar");
        tabelmapel.addColumn("Berat");
        tabelmapel.addColumn("Kategori");
        tabelmapel.addColumn("Jumlah");
        tabelmapel.addColumn("Total Bayar");
        
        try {
            connection = Koneksi.connection();
            String sql = "select a.no_nota,a.no_pelanggan,a.tgl_masuk,a.tgl_selesai,a.status,a.status_bayar,a.tgl_bayar,b.berat,b.kategori,b.jumlah,b.total_bayar from transaksi a, data_transaksi b where a.no_nota=b.no_nota";
            java.sql.Statement stmt = connection.createStatement();
            java.sql.ResultSet rslt = stmt.executeQuery(sql);
            while (rslt.next()) {
                Object[] o =new Object[11];
                o[0] = rslt.getString("no_nota");
                o[1] = rslt.getString("no_pelanggan");
                o[2] = rslt.getString("tgl_masuk");
                o[3] = rslt.getString("tgl_selesai");
                o[4] = rslt.getString("status");
                o[5] = rslt.getString("status_bayar");
                o[6] = rslt.getString("tgl_bayar");
                o[7] = rslt.getString("berat");
                o[8] = rslt.getString("kategori");
                o[9] = rslt.getString("jumlah");
                o[10] = rslt.getString("total_bayar");
                tabelmapel.addRow(o);
            }
            tabel_T.setModel(tabelmapel);

        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Gagal memuat data");
        }
    
        }
    
