    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
int bulan = 0;
        if(c_bulan.getSelectedItem().equals("Januari")){
            bulan = 1;
        }
        else if(c_bulan.getSelectedItem().equals("Februari")){
            bulan = 2;
        }
        else if(c_bulan.getSelectedItem().equals("Maret")){
            bulan = 3;
        }
        else if(c_bulan.getSelectedItem().equals("April")){
            bulan = 4;
        }
        else if(c_bulan.getSelectedItem().equals("Mei")){
            bulan = 5;
        }
        else if(c_bulan.getSelectedItem().equals("Juni")){
            bulan = 6;
        }
        else if(c_bulan.getSelectedItem().equals("Juli")){
            bulan = 7;
        }
        else if(c_bulan.getSelectedItem().equals("Agustus")){
            bulan = 8;
        }
        else if(c_bulan.getSelectedItem().equals("September")){
            bulan = 9;
        }
        else if(c_bulan.getSelectedItem().equals("Oktober")){
            bulan = 10;
        }
        else if(c_bulan.getSelectedItem().equals("November")){
            bulan = 11;
        }
        else if(c_bulan.getSelectedItem().equals("Desember")){
            bulan = 12;
        }
        try{
       String NamaFile = "src/report/pelanggan.jasper"; 
       Class.forName("com.mysql.jdbc.Driver").newInstance();
       Connection koneksi = DriverManager.getConnection("jdbc:mysql://localhost/Laundry","root","");
       @SuppressWarnings("rawtypes")
       HashMap hash = new HashMap(2);
       //mengambil parameter dari ireport
       hash.put("bulann", bulan);
       hash.put("tahunn", c_tahun.getSelectedItem());
       //hash.put("total", txt_hitung.getText());
       
       File file = new File(NamaFile);
       JasperReport jasperReport = (JasperReport) JRLoader.loadObject(file);
       JasperPrint jasperPrint = JasperFillManager.fillReport(jasperReport, hash,koneksi);
       JasperViewer.viewReport(jasperPrint,false);
   }catch(Exception ex){
       System.out.println(ex);
   }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

