private void tampilkan_dataP() {
        
        DefaultTableModel tabelmapel = new DefaultTableModel();
        tabelmapel.addColumn("No Pelanggan");
        tabelmapel.addColumn("Nama");
        tabelmapel.addColumn("No HP");
        tabelmapel.addColumn("Alamat");
        tabelmapel.addColumn("Tanggal");
        
        int bulan = 0;
        if(c_bulan.getSelectedItem().equals("Januari")){
            bulan = 1;
        }
        else if(c_bulan.getSelectedItem().equals("Februari")){
            bulan = 2;
        }
        else if(c_bulan.getSelectedItem().equals("Maret")){
            bulan = 3;
        }
        else if(c_bulan.getSelectedItem().equals("April")){
            bulan = 4;
        }
        else if(c_bulan.getSelectedItem().equals("Mei")){
            bulan = 5;
        }
        else if(c_bulan.getSelectedItem().equals("Juni")){
            bulan = 6;
        }
        else if(c_bulan.getSelectedItem().equals("Juli")){
            bulan = 7;
        }
        else if(c_bulan.getSelectedItem().equals("Agustus")){
            bulan = 8;
        }
        else if(c_bulan.getSelectedItem().equals("September")){
            bulan = 9;
        }
        else if(c_bulan.getSelectedItem().equals("Oktober")){
            bulan = 10;
        }
        else if(c_bulan.getSelectedItem().equals("November")){
            bulan = 11;
        }
        else if(c_bulan.getSelectedItem().equals("Desember")){
            bulan = 12;
        }
        

        try {
            connection = Koneksi.connection();
            String sql = "select * from pelanggan where MONTH(tanggal) = '"+bulan+"' And YEAR(tanggal) = '"+c_tahun.getSelectedItem()+"'";
            java.sql.Statement stmt = connection.createStatement();
            java.sql.ResultSet rslt = stmt.executeQuery(sql);
            while (rslt.next()) {
                Object[] o =new Object[5];
                o[0] = rslt.getString("no_pelanggan");
                o[1] = rslt.getString("nama");
                o[2] = rslt.getString("no_hp");
                o[3] = rslt.getString("alamat");
                o[4] = rslt.getString("tanggal");
                tabelmapel.addRow(o);
            }
            t_pelanggan.setModel(tabelmapel);

        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Gagal memuat data pelanggan");
        }
    
        }

