    private void b_hapusbulanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b_hapusbulanActionPerformed
T_TransaksiA frame = null;
if(JOptionPane.showConfirmDialog(frame, "Apakah anda yakin ingin menghapus semua data pada bulan ini?","Hapus data",
                JOptionPane.YES_NO_OPTION)==JOptionPane.YES_OPTION){
	
        try {
            connection = Koneksi.connection();
            String sql = "delete from transaksi where MONTH(tgl_masuk) = '"+c_bulan.getSelectedItem()+"' And YEAR(tgl_masuk) = '"+c_tahun.getSelectedItem()+"'";
            java.sql.Statement stmt = connection.createStatement();
            stmt.executeUpdate(sql);
            stmt.close();
            JOptionPane.showMessageDialog(null, "Data berhasil dihapus..","Hapus Data",JOptionPane.INFORMATION_MESSAGE);
            ct.isiTable();
            ct.reset();
            } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Gagal menghapus data");
        }
    
        
    }        
// TODO add your handling code here:
    }//GEN-LAST:event_b_hapusbulanActionPerformed

