    private void tampilkan_dataT() {
        
        DefaultTableModel tabelmapel = new DefaultTableModel();
        tabelmapel.addColumn("No Nota");
        tabelmapel.addColumn("No Pelanggan");
        tabelmapel.addColumn("Tanggal Masuk");
        tabelmapel.addColumn("Tanggal Selesai");
        tabelmapel.addColumn("Status");
        tabelmapel.addColumn("Status Bayar");
        tabelmapel.addColumn("Tanggal bayar");
       
        try {
connection = Koneksi.connection();
            String sql = "select* from transaksi where MONTH(tgl_masuk) = '"+c_bulan.getSelectedItem()+"' And YEAR(tgl_masuk) = '"+c_tahun.getSelectedItem()+"'";
            java.sql.Statement stmt = connection.createStatement();
            java.sql.ResultSet rslt = stmt.executeQuery(sql);
            while (rslt.next()) {
                Object[] o =new Object[9];
                o[0] = rslt.getString("no_nota");
                o[1] = rslt.getString("no_pelanggan");
                o[2] = rslt.getDate("tgl_masuk");
                o[3] = rslt.getDate("tgl_selesai");
                o[4] = rslt.getString("status");
                o[5] = rslt.getString("status_bayar");
                o[6] = rslt.getString("tgl_bayar");
                tabelmapel.addRow(o);
            }
            tabel_T.setModel(tabelmapel);

        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Gagal memuat data transaksi");
        }
    
        }
    
    private void tampilkan_data_transaksi() {
                   
        try {
            connection = Koneksi.connection();
            String sql = "select * from data_transaksi where no_nota ='"+txt_nota.getText()+"'";
            java.sql.Statement stmt = connection.createStatement();
            java.sql.ResultSet rslt = stmt.executeQuery(sql);
            while (rslt.next()) {
                String h,i,j,k,l;
                
                h = rslt.getString("no_nota");
                j = rslt.getString("berat");
                i = rslt.getString("kategori");
                k = rslt.getString("jumlah");
                l = rslt.getString("total_bayar");
                
                txt_berat.setText(j);
                txt_jumlah.setText(k);
                txt_total.setText(l);
                area.setText(i);
                

                
                }
           } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Gagal memuat data transaksi");
        }
    
        }
    
